import * as React from 'react';
import dva, { connect } from 'dva';
import { Text, View } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { MaterialCommunityIcons } from '@expo/vector-icons';

import CalendarPage from './Calendar/CalendarPage';
import SummaryPage from './Summary/SummaryPage';
import AccountPage from './Account/AccountPage';
import SearchPage from './Search/SearchPage';
import ProvideDataPage from './Other/ProvideDataPage';
import CreateTask from './Calendar/CreateTaskPage';
import TodoStore from './data/TodoStore';
import MonthlyExpense from './RecordList/MonthlyExpense';
import DailyExpense from './RecordList/DailyExpense';
import CameraComponent from './Common/CameraComponent';

const Stack = createStackNavigator();
const Tab = createBottomTabNavigator();

class MainPage extends React.PureComponent {
  state = {

  }

  HomeStack() {
    return (
      <Stack.Navigator
        initialRouteName="Calendar"
        screenOptions={{
          headerStyle: { backgroundColor: 'orange' },
          headerTintColor: '#fff',
          headerTitleStyle: { fontWeight: 'bold' }
        }}>
        <Stack.Screen
          name="Calendar"
          component={CalendarPage}
          options={{ title: 'Calendar' }} />
        <Stack.Screen
          name="Search"
          component={SearchPage}
          options={{ title: 'Search' }} />
        <Stack.Screen
          name="Account"
          component={AccountPage}
          options={{ title: 'Account' }} />
        <Stack.Screen
          name="Summary"
          component={SummaryPage}
          options={{ title: 'Summary' }} />
        <Stack.Screen
          name="Other"
          component={ProvideDataPage}
          options={{ title: 'Other' }} />

        <Stack.Screen name="createTask" component={CreateTask} />
        <Stack.Screen name="camera" component={CameraComponent} />
        <Stack.Screen name="dailyExpense" component={DailyExpense} />
        <Stack.Screen name="monthlyExpense" component={MonthlyExpense} />
      </Stack.Navigator>

    );
  }

  SearchStack() {
    return (
      <Stack.Navigator
        initialRouteName="Search"
        screenOptions={{
          headerStyle: { backgroundColor: 'orange' },
          headerTintColor: '#fff',
          headerTitleStyle: { fontWeight: 'bold' }
        }}>
        <Stack.Screen
          name="Calendar"
          component={CalendarPage}
          options={{ title: 'Calendar' }} />
        <Stack.Screen
          name="Search"
          component={SearchPage}
          options={{ title: 'Search' }} />
        <Stack.Screen
          name="Account"
          component={AccountPage}
          options={{ title: 'Account' }} />
        <Stack.Screen
          name="Summary"
          component={SummaryPage}
          options={{ title: 'Summary' }} />
        <Stack.Screen
          name="Other"
          component={ProvideDataPage}
          options={{ title: 'Other' }} />
        <Stack.Screen name="createTask" component={CreateTask} />
        <Stack.Screen name="camera" component={CameraComponent} />
        <Stack.Screen name="dailyExpense" component={DailyExpense} />
        <Stack.Screen name="monthlyExpense" component={MonthlyExpense} />
      </Stack.Navigator>

    );
  }
  AccountStack() {
    return (
      <Stack.Navigator
        initialRouteName="Account"
        screenOptions={{
          headerStyle: { backgroundColor: 'orange' },
          headerTintColor: '#fff',
          headerTitleStyle: { fontWeight: 'bold' }
        }}>
        <Stack.Screen
          name="Calendar"
          component={CalendarPage}
          options={{ title: 'Calendar' }} />
        <Stack.Screen
          name="Search"
          component={SearchPage}
          options={{ title: 'Search' }} />
        <Stack.Screen
          name="Account"
          component={AccountPage}
          options={{ title: 'Account' }} />
        <Stack.Screen
          name="Summary"
          component={SummaryPage}
          options={{ title: 'Summary' }} />
        <Stack.Screen
          name="Other"
          component={ProvideDataPage}
          options={{ title: 'Other' }} />
        <Stack.Screen name="createTask" component={CreateTask} />
        <Stack.Screen name="dailyExpense" component={DailyExpense} />
        <Stack.Screen name="monthlyExpense" component={MonthlyExpense} />
      </Stack.Navigator>
    );
  }

  SummaryStack() {
    return (
      <Stack.Navigator
        initialRouteName="Summary"
        screenOptions={{
          headerStyle: { backgroundColor: 'orange' },
          headerTintColor: '#fff',
          headerTitleStyle: { fontWeight: 'bold' }
        }}>
        <Stack.Screen
          name="Calendar"
          component={CalendarPage}
          options={{ title: 'Calendar' }} />
        <Stack.Screen
          name="Account"
          component={AccountPage}
          options={{ title: 'Account' }} />
        <Stack.Screen
          name="Search"
          component={SearchPage}
          options={{ title: 'Search' }} />
        <Stack.Screen
          name="Summary"
          component={SummaryPage}
          options={{ title: 'Summary' }} />
        <Stack.Screen
          name="Other"
          component={ProvideDataPage}
          options={{ title: 'Other' }} />
        <Stack.Screen name="createTask" component={CreateTask} />
        <Stack.Screen name="dailyExpense" component={DailyExpense} />
        <Stack.Screen name="monthlyExpense" component={MonthlyExpense} />
      </Stack.Navigator>
    );
  }
  OtherStack() {
    return (
      <Stack.Navigator
        initialRouteName="Other"
        screenOptions={{
