import React, { useState, Fragment } from 'react';
import { StyleSheet, View, SafeAreaView, Text, TouchableOpacity } from 'react-native';
import { Calendar } from 'react-native-calendars';
import moment from 'moment';
import _ from 'lodash';

const CalendarsScreen = () => {
  const [selected, setSelected] = useState('');

  const onDayPress = (day) => {
    setSelected(day.dateString);
  };

  const getDisabledDates = (startDate, endDate, daysToDisable) => {
    const disabledDates = {};
    const start = moment(startDate);
    const end = moment(endDate);
    for (let m = moment(start); m.diff(end, 'days') <= 0; m.add(1, 'days')) {
      if (_.includes(daysToDisable, m.weekday())) {
        disabledDates[m.format('YYYY-MM-DD')] = { disabled: true };
      }
    }
    return disabledDates;
  };

  const renderCalendarWithSelectableDate = () => {
    return (
      <Fragment>
        <Fragment></Fragment>
        <Calendar
          current={new Date()}
          style={styles.calendar}
          hideExtraDays
          onDayPress={onDayPress}
          markedDates={{
            [selected]: {
              selected: true,
              disableTouchEvent: true,
              selectedColor: 'blue',
              selectedTextColor: 'white',
            },
          }}
        />
        <TouchableOpacity
          onPress={() =>
            navigation.navigate('createTask', {
              // updateCurrentTask: this._updateCurrentTask,
              currentDate,
              // createNewCalendar: this._createNewCalendar,
            })
          }
          style={styles.viewTask}
        >
          <Image
            source={require('../../assets/plus.png')}
            style={{
              height: 30,
              width: 30,
            }}
          />
        </TouchableOpacity>
      </Fragment>
    );
  };

  return (
    <SafeAreaView showsVerticalScrollIndicator={false}>
      {renderCalendarWithSelectableDate()}
    </SafeAreaView>
  );
};

export default CalendarsScreen;

const styles = StyleSheet.create({
  calendar: {
    marginBottom: 10,
  },
  text: {
    textAlign: 'center',
    padding: 10,
    backgroundColor: 'lightgrey',
    fontSize: 16,
  },
});
