import React, { Component } from 'react';
import dva, { connect } from 'dva';
import {
  Text,
  Image,
  View,
  SafeAreaView,
  TouchableOpacity,
  Dimensions,
  ScrollView,
  TextInput,
  Keyboard,
  Switch,
  StyleSheet,
  Alert,
} from 'react-native';
import { Context } from '../../utils/Context';
import DateTimePicker from 'react-native-modal-datetime-picker';
import moment from 'moment';
import uuid from 'uuid';

class ExpensePage extends React.PureComponent {
  state = {
    selectedDay: {
      [`${moment().format('YYYY')}-${moment().format('MM')}-${moment().format(
        'DD'
      )}`]: {
        selected: true,
        selectedColor: '#2E66E7',
      },
    },
    currentDay: moment().format(),
    taskText: '',
    notesText: '',
    keyboardHeight: 0,
    visibleHeight: Dimensions.get('window').height,
    alarmTime: moment().format(),
    isDateTimePickerVisible: false,
    timeType: '',
    creatTodo: {},
    createEventAsyncRes: '',
  }

  componentWillUnmount() {
    Keyboard.removeListener('keyboardDidShow', this._keyboardDidShow);
    Keyboard.removeListener('keyboardDidHide', this._keyboardDidHide);
  }

  _keyboardDidShow = e => {
    this.setState({
      keyboardHeight: e.endCoordinates.height,
      visibleHeight:
        Dimensions.get('window').height - e.endCoordinates.height - 30,
    });
  };

  _keyboardDidHide = () => {
    this.setState({
      visibleHeight: Dimensions.get('window').height,
    });
  };
  _showDateTimePicker = () => this.setState({ isDateTimePickerVisible: true });

  _hideDateTimePicker = () => this.setState({ isDateTimePickerVisible: false });

  _handleCreateEventData = async value => {
    const {
      state: {
        taskText,
        notesText,
      },
      props: { navigation, route },
    } = this;
    const { currentDate, updateCurrentTask } = route.params;
    const createTodo = {
      key: uuid(),
      date: currentDate,
      todoList: [
        {
          key: uuid(),
          title: taskText,
          notes: notesText,
          color: `rgb(${Math.floor(
            Math.random() * Math.floor(256)
          )},${Math.floor(Math.random() * Math.floor(256))},${Math.floor(
            Math.random() * Math.floor(256)
          )})`,
        },
      ],
      markedDot: {
        date: currentDate,
        dots: [
          {
            key: uuid(),
            color: '#2E66E7',
            selectedDotColor: '#2E66E7',
          },
        ],
      },
    };
    await value.updateTodo(createTodo);
    await updateCurrentTask(currentDate);
    navigation.navigate('Calendar');
  };

  // Local path to file on the device
  render() {
    const { dispatch, effects } = this.props;
    const {
      taskText,
      notesText,
      alarmTime,
      isDateTimePickerVisible, } = this.state;
    return (

      <Context.Consumer>
        {value => (
          <>
            <DateTimePicker
              isVisible={isDateTimePickerVisible}
              onConfirm={this._handleDatePicked}
              onCancel={this._hideDateTimePicker}
              mode="time"
            />

            <View style={styles.container}>

              <ScrollView
                contentContainerStyle={{
                  paddingBottom: 100,
                }}
              >
                <View style={styles.taskContainer}>
                  <TextInput
                    style={styles.title}
                    onChangeText={text => this.setState({ taskText: text })}
                    value={taskText}
                    placeholder="What do you need to do?"
                  />
                  <Text
                    style={{
                      fontSize: 14,
                      color: '#BDC6D8',
                      marginVertical: 10,
                    }}
                  >
                    Suggestion
                </Text>
                  <View style={{ flexDirection: 'row' }}>
                    <View style={styles.readBook}>
                      <Text style={{ textAlign: 'center', fontSize: 14 }}>
                        Read book
                    </Text>
                    </View>
                    <View style={styles.design}>
                      <Text style={{ textAlign: 'center', fontSize: 14 }}>
                        Design
                    </Text>
                    </View>
                    <View style={styles.learn}>
                      <Text style={{ textAlign: 'center', fontSize: 14 }}>
                        Learn
                    </Text>
                    </View>
                  </View>
                  <View style={styles.notesContent} />
                  <View>
                    <Text style={styles.notes}>Notes</Text>
                    <TextInput
                      style={{
                        height: 25,
                        fontSize: 19,
                        marginTop: 3,
                      }}
                      onChangeText={text =>
                        this.setState({ notesText: text })
                      }
                      value={notesText}
                      placeholder="Enter notes about the task."
                    />
                  </View>
                  <View style={styles.seperator} />
                  <View>
                    <Text
                      style={{
                        color: '#9CAAC4',
                        fontSize: 16,
                        fontWeight: '600',
                      }}
                    >
                      Times
                  </Text>
                    <TouchableOpacity
                      onPress={() => this._showDateTimePicker()}
                      style={{
                        height: 25,
                        marginTop: 3,
                      }}
                    >
                      <Text style={{ fontSize: 19 }}>
                        {moment(alarmTime).format('h:mm A')}
                      </Text>
                    </TouchableOpacity>
                  </View>
                  <View style={styles.seperator} />
                  <View
                    style={{
                      flexDirection: 'row',
                      justifyContent: 'space-between',
                      alignItems: 'center',
                    }}
                  >
                    <View>
                      <Text
                        style={{
                          color: '#9CAAC4',
                          fontSize: 16,
                          fontWeight: '600',
                        }}
                      >
                        Alarm
                    </Text>
                      <View
                        style={{
                          height: 25,
                          marginTop: 3,
                        }}
                      >
                        <Text style={{ fontSize: 19 }}>
                          {moment(alarmTime).format('h:mm A')}
                        </Text>
                      </View>
                    </View>
                  </View>
                </View>
                <TouchableOpacity
                  disabled={taskText === ''}
                  style={[
                    styles.createTaskButton,
                    {
                      backgroundColor:
                        taskText === ''
                          ? 'rgba(46, 102, 231,0.5)'
                          : '#2E66E7',
                    },
                  ]}
                  onPress={async () => {
                    this._handleCreateEventData(value);
                  }}
                >
                  <Text
                    style={{
                      fontSize: 18,
                      textAlign: 'center',
                      color: '#fff',
                    }}
                  >
                    ADD YOUR TASK
                </Text>
                </TouchableOpacity>
              </ScrollView>
            </View>
          </>
        )}
      </Context.Consumer>
    )
  }
}
export default connect(state => ({
  loading: state.loading,
  effects: state.loading.effects
}))(ExpensePage);
