import React, { useState, Fragment } from 'react';
import dva, { connect } from 'dva';
import { StyleSheet, View, Image, SafeAreaView, Text, TouchableOpacity, Dimensions, ScrollView } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';
class AccountPage extends React.PureComponent {
  state = {

  }

  componentWillMount() {
    this._handleDeletePreviousDayTask();
  }

  _handleDeletePreviousDayTask = async () => {
    const { route } = this.props;
    const { currentDate } = route.params;
    try {
      const value = await AsyncStorage.getItem('TODO');

      if (value !== null) {
        const todoList = JSON.parse(value);
        const todayDate = `${moment().format('YYYY')}-${moment().format(
          'MM'
        )}-${moment().format('DD')}`;
        const checkDate = moment(todayDate);
        await todoList.filter(item => {
          const currDate = moment(item.date);
          const checkedDate = checkDate.diff(currDate, 'days');
          if (checkedDate > 0) {
            item.todoList.forEach(async listValue => {
              try {
                await Calendar.deleteEventAsync(
                  listValue.alarm.createEventAsyncRes.toString()
                );
              } catch (error) {
                console.log(error);
              }
            });
            return false;
          }
          return true;
        });

        await AsyncStorage.setItem('TODO', JSON.stringify(updatedList));
        this._updateCurrentTask(currentDate);
      }
    } catch (error) {
      // Error retrieving data
    }
  };

  _updateCurrentTask = async currentDate => {
    try {
      const value = await AsyncStorage.getItem('TODO');
      if (value !== null) {
        const todoList = JSON.parse(value);
        const markDot = todoList.map(item => item.markedDot);
        const todoLists = todoList.filter(item => {
          if (currentDate === item.date) {
            return true;
          }
          return false;
        });
        console.log(todoList);
        if (todoLists.length !== 0) {
          this.setState({
            markedDate: markDot,
            todoList: todoLists[0].todoList,
          });
        } else {
          this.setState({
            markedDate: markDot,
            todoList: [],
          });
        }
      }
    } catch (error) {
      // Error retrieving data
    }
  };
  // Local path to file on the device
  render() {
    const { dispatch, effects } = this.props;
    console.log(this.props);
    var todoList = [];
    return (
      <View
        style={{
          width: '100%',
          height: Dimensions.get('window').height - 170,
        }}
      >
        Daily
        <ScrollView
          contentContainerStyle={{
            paddingBottom: 20,
          }}
        >
          {todoList.map(item => (
            <TouchableOpacity
              onPress={() => {
                this.setState(
                  {
                    selectedTask: item,
                    isModalVisible: true,
                  },
                  () => {
                    // this._getEvent();
                  }
                );
              }}
              key={item.key}
              style={styles.taskListContent}
            >
              <View
                style={{
                  marginLeft: 13,
                }}
              >
                <View
                  style={{
                    flexDirection: 'row',
                    alignItems: 'center',
                  }}
                >
                  <View
                    style={{
                      height: 12,
                      width: 12,
                      borderRadius: 6,
                      backgroundColor: item.color,
                      marginRight: 8,
                    }}
                  />
                  <Text
                    style={{
                      color: '#554A4C',
                      fontSize: 20,
                      // fontWeight: '700',
                    }}
                  >
                    {item.category}:{item.item}
                  </Text>
                </View>
                <View>
                  <View
                    style={{
                      flexDirection: 'row',
                      marginLeft: 20,
                    }}
                  >
                    <Text
                      style={{
                        color: '#BBBBBB',
                        fontSize: 14,
                      }}
                    >
                      {item.notes}
                    </Text>
                  </View>
                </View>
              </View>
              {/* <View
              style={{
                height: 80,
                width: 5,
                backgroundColor: item.color,
                borderRadius: 5,
              }}
            /> */}
            </TouchableOpacity>
          ))}
        </ScrollView>
      </View>
    )
  }
}
export default connect(state => ({
  loading: state.loading,
  effects: state.loading.effects
}))(AccountPage);
