import React, { useState, Fragment } from 'react';
import dva, { connect } from 'dva';
import { StyleSheet, View, Image, SafeAreaView, Text, TouchableOpacity, Dimensions, ScrollView } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';
class AccountPage extends React.PureComponent {
  state = {

  }
  // Local path to file on the device
  render() {
    const { route } = this.props;
    const { todoList, currentDate } = route.params;
    return (
      <View
        style={{
          width: '100%',
          height: Dimensions.get('window').height - 170,
        }}
      >
        <ScrollView
          contentContainerStyle={{
            paddingBottom: 20,
          }}
        >
          {todoList.map(item => (
            <TouchableOpacity
              onPress={() => {
                this.setState(
                  {
                    selectedTask: item,
                    isModalVisible: true,
                  },
                  () => {
                    // this._getEvent();
                  }
                );
              }}
              key={item.key}
              style={styles.taskListContent}
            >
              <View
                style={{
                  marginLeft: 13,
                }}
              >
                <View
                  style={{
                    flexDirection: 'row',
                    alignItems: 'center',
                  }}
                >
                  <View
                    style={{
                      height: 12,
                      width: 12,
                      borderRadius: 6,
                      backgroundColor: item.color,
                      marginRight: 8,
                    }}
                  />
                  <Text
                    style={{
                      color: '#554A4C',
                      fontSize: 20,
                      // fontWeight: '700',
                    }}
                  >
                    {item.category}:{item.item}
                  </Text>
                </View>
                <View>
                  <View
                    style={{
                      flexDirection: 'row',
                      marginLeft: 20,
                    }}
                  >
                    <Text
                      style={{
                        color: '#BBBBBB',
                        fontSize: 14,
                      }}
                    >
                      {item.notes}
                    </Text>
                  </View>
                </View>
              </View>
              {/* <View
              style={{
                height: 80,
                width: 5,
                backgroundColor: item.color,
                borderRadius: 5,
              }}
            /> */}
            </TouchableOpacity>
          ))}
        </ScrollView>
      </View>
    )
  }
}
export default connect(state => ({
  loading: state.loading,
  effects: state.loading.effects
}))(AccountPage);
