class ExpensePage extends React.PureComponent {
  state = {
    selectedDay: {
      [`${moment().format('YYYY')}-${moment().format('MM')}-${moment().format(
        'DD'
      )}`]: {
        selected: true,
        selectedColor: '#2E66E7',
      },
    },
    currentDay: moment().format(),
    amount: '',
    notesText: '',
    category: '',
    keyboardHeight: 0,
    visibleHeight: Dimensions.get('window').height,
    alarmTime: moment().format(),
    isDateTimePickerVisible: false,
    timeType: '',
    creatTodo: {},
    createEventAsyncRes: '',
    hasPermission: null,
    cameraType: Camera.Constants.Type.back,
  }

  async componentDidMount() {
    this.getPermissionAsync()
  }
  getPermissionAsync = async () => {
    // Camera roll Permission 
    if (Platform.OS === 'ios') {
      const { status } = await Permissions.askAsync(Permissions.CAMERA_ROLL);
      if (status !== 'granted') {
        alert('Sorry, we need camera roll permissions to make this work!');
      }
    }
    // Camera Permission
    const { status } = await Permissions.askAsync(Permissions.CAMERA);
    this.setState({ hasPermission: status === 'granted' });
  }

  componentWillUnmount() {
    Keyboard.removeListener('keyboardDidShow', this._keyboardDidShow);
    Keyboard.removeListener('keyboardDidHide', this._keyboardDidHide);
  }

  useEffect = () => {
    (async () => {
      const { status } = await Camera.requestPermissionsAsync();
      setHasPermission(status === 'granted');
    })();
  }


  _keyboardDidShow = e => {
    this.setState({
      keyboardHeight: e.endCoordinates.height,
      visibleHeight:
        Dimensions.get('window').height - e.endCoordinates.height - 30,
    });
  };

  _keyboardDidHide = () => {
    this.setState({
      visibleHeight: Dimensions.get('window').height,
    });
  };
  _showDateTimePicker = () => this.setState({ isDateTimePickerVisible: true });

  _hideDateTimePicker = () => this.setState({ isDateTimePickerVisible: false });

  _handleCreateEventData = async value => {
    const { state: { amount, notesText, }, props: { navigation, route } } = this;
    const { currentDate, updateCurrentTask } = route.params;
    const createTodo = {
      key: uuid(),
      date: currentDate,
      todoList: [
        {
          key: uuid(),
          title: amount,
          notes: notesText,
          type: "Expense",
          color: `rgb(${Math.floor(
            Math.random() * Math.floor(256)
          )},${Math.floor(Math.random() * Math.floor(256))},${Math.floor(
            Math.random() * Math.floor(256)
          )})`,
        },
      ],
      markedDot: {
        date: currentDate,
        dots: [
          {
            key: uuid(),
            color: '#2E66E7',
            selectedDotColor: '#2E66E7',
          },
        ],
      },
    };
    await value.updateTodo(createTodo);
    await updateCurrentTask(currentDate);
    navigation.navigate('Calendar');
  };
  handleCameraType = () => {
    const { cameraType } = this.state

    this.setState({
      cameraType:
        cameraType === Camera.Constants.Type.back
          ? Camera.Constants.Type.front
          : Camera.Constants.Type.back
    })
  }

  takePicture = async () => {
    if (this.camera) {
      let photo = await this.camera.current.takePictureAsync();
    }
  }

  pickImage = async () => {
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
    });
  }
  //https://reactnativemaster.com/react-native-camera-expo-example
  // Local path to file on the device
  render() {
    const { amount, notesText, category, hasPermission } = this.state;
    console.log(hasPermission);
    return (
      <Context.Consumer>
        {value => (
          <>
            <View style={styles.container}>
              <ScrollView
                contentContainerStyle={{
                  paddingBottom: 100,
                }}
              >
                <View style={styles.taskContainer}>
                  <View style={{ flexDirection: 'row' }}>
                    <Text style={styles.date}>Date: {this.props.route.params.currentDate}</Text>
                  </View>
                  {hasPermission === null ? <View /> : (
                    hasPermission === false ? <Text>No access to camera</Text> :
                      <View style={{ flex: 1 }}>
                        <Camera style={{ flex: 1 }} type={this.state.cameraType}
                          ref={ref => {
                            this.camera = ref;
                          }}>
                          <View style={{ flex: 1, flexDirection: "row", justifyContent: "space-between", margin: 20 }}>
                            <TouchableOpacity
                              style={{
                                alignSelf: 'flex-end',
                                alignItems: 'center',
                                backgroundColor: 'transparent',
                              }}>
                              <Ionicons
                                name="ios-photos"
                                style={{ color: "#fff", fontSize: 40 }}
                              />
                            </TouchableOpacity>
                            <TouchableOpacity
                              style={{
                                alignSelf: 'flex-end',
                                alignItems: 'center',
                                backgroundColor: 'transparent',
                              }}
                              onPress={() => this.takePicture()}>
                              <FontAwesome
                                name="camera"
                                style={{ color: "#fff", fontSize: 40 }}
                              />
                            </TouchableOpacity>
                            <TouchableOpacity
                              style={{
                                alignSelf: 'flex-end',
                                alignItems: 'center',
                                backgroundColor: 'transparent',
                              }}
                              onPress={() => this.handleCameraType()}>
                              <MaterialCommunityIcons
                                name="camera-switch"
                                style={{ color: "#fff", fontSize: 40 }}
                              />
                            </TouchableOpacity>
                          </View>
                        </Camera>
                      </View>
                  )}
                  <TouchableOpacity
                    style={{
                      alignSelf: 'flex-end',
                      alignItems: 'center',
                      backgroundColor: 'transparent',
                    }}
                    onPress={() => this.handleCameraType()}
                  >
                    <MaterialCommunityIcons
                      name="camera-switch"
                      style={{ color: "#fff", fontSize: 40 }}
                    />
                  </TouchableOpacity>
                  <View>
                    <Text style={styles.notes}>Category</Text>
                    <TextInput
                      style={{
                        height: 25,
                        fontSize: 19,
                        marginTop: 3,
                      }}
                      onChangeText={text =>
                        this.setState({ category: text })
                      }
                      value={category}
                      placeholder="Category"
                    />
                    <Picker
                      selectedValue={this.state.category}
                      style={{ height: 50, width: 100 }}
                      onValueChange={(itemValue, itemIndex) => this.setState({ category: itemValue })}>
                      <Picker.Item label="Java" value="java" />
                      <Picker.Item label="JavaScript" value="js" />
                    </Picker>
                  </View>
                  <View>
                    <Text style={styles.notes}>Amount</Text>
                    <TextInput
                      style={{
                        height: 25,
                        fontSize: 19,
                        marginTop: 3,
                      }}
                      onChangeText={amount => this.setState({ amount: amount })}
                      value={amount}
                      placeholder="Amount"
                    />
                  </View>
                  {/* <View style={{ flexDirection: 'row' }}>
                    <View style={styles.readBook}>
                      <Text style={{ textAlign: 'center', fontSize: 14 }}>
                        Read book
                    </Text>
                    </View>
                    <View style={styles.design}>
                      <Text style={{ textAlign: 'center', fontSize: 14 }}>
                        Design
                    </Text>
                    </View>
                    <View style={styles.learn}>
                      <Text style={{ textAlign: 'center', fontSize: 14 }}>
                        Learn
                    </Text>
                    </View>
                  </View> */}
                  <View style={styles.notesContent} />
                  <View>
                    <Text style={styles.notes}>Notes</Text>
                    <TextInput
                      style={{
                        height: 25,
                        fontSize: 19,
                        marginTop: 3,
                      }}
                      onChangeText={text =>
                        this.setState({ notesText: text })
                      }
                      value={notesText}
                      placeholder="Enter notes about the task."
                    />
                  </View>
                </View>
                <TouchableOpacity
                  disabled={amount === ''}
                  style={[
                    styles.createTaskButton,
                    {
                      backgroundColor:
                        amount === ''
                          ? 'rgba(46, 102, 231,0.5)'
                          : '#2E66E7',
                    },
                  ]}
                  onPress={async () => {
                    this._handleCreateEventData(value);
                  }}
                >
                  <Text
                    style={{
                      fontSize: 18,
                      textAlign: 'center',
                      color: '#fff',
                    }}
                  >
                    ADD YOUR EXPENSE
                </Text>
                </TouchableOpacity>
              </ScrollView>
            </View>
          </>
        )}
      </Context.Consumer>
    )
  }
}
export default connect(state => ({
  loading: state.loading,
  effects: state.loading.effects
}))(ExpensePage);
