import React, { useState, Fragment } from 'react';
import dva, { connect } from 'dva';
import { StyleSheet, View, Image, SafeAreaView, Text, TouchableOpacity, Dimensions, ScrollView } from 'react-native';
import { Calendar } from 'react-native-calendars';
import moment from 'moment';
import _ from 'lodash';



class CalendarPage extends React.PureComponent {
  state = {
    selected: '',
    datesWhitelist: [
      {
        start: moment(),
        end: moment().add(365, 'days'), // total 4 days enabled
      },
    ],
    todoList: [],
    markedDate: [],
    currentDate: `${moment().format('YYYY')}-${moment().format(
      'MM'
    )}-${moment().format('DD')}`,
    isModalVisible: false,
    selectedTask: null,
    isDateTimePickerVisible: false,
  }

  componentWillMount() {
    console.log("here");
    this._handleDeletePreviousDayTask();
  }

  componentDidUpdate(){
    console.log("this update");
    console.log(this.props);
  }
  _handleDeletePreviousDayTask = async () => {
    const { currentDate } = this.state;
    try {
      const value = await AsyncStorage.getItem('TODO');

      if (value !== null) {
        const todoList = JSON.parse(value);
        const todayDate = `${moment().format('YYYY')}-${moment().format(
          'MM'
        )}-${moment().format('DD')}`;
        const checkDate = moment(todayDate);
        await todoList.filter(item => {
          const currDate = moment(item.date);
          const checkedDate = checkDate.diff(currDate, 'days');
          if (checkedDate > 0) {
            item.todoList.forEach(async listValue => {
              try {
                await Calendar.deleteEventAsync(
                  listValue.alarm.createEventAsyncRes.toString()
                );
              } catch (error) {
                console.log(error);
              }
            });
            return false;
          }
          return true;
        });

        await AsyncStorage.setItem('TODO', JSON.stringify(updatedList));
        this._updateCurrentTask(currentDate);
      }
    } catch (error) {
      // Error retrieving data
    }
  };

  onDayPress = (day) => {
    this.setState({
      selected: day.dateString
    })
  };

  getDisabledDates = (startDate, endDate, daysToDisable) => {
    const disabledDates = {};
    const start = moment(startDate);
    const end = moment(endDate);
    for (let m = moment(start); m.diff(end, 'days') <= 0; m.add(1, 'days')) {
      if (_.includes(daysToDisable, m.weekday())) {
        disabledDates[m.format('YYYY-MM-DD')] = { disabled: true };
      }
    }
    return disabledDates;
  };


  _updateCurrentTask = async currentDate => {
    try {
      const value = await AsyncStorage.getItem('TODO');
      console.log("value", value);
      if (value !== null) {
        const todoList = JSON.parse(value);
        const markDot = todoList.map(item => item.markedDot);
        const todoLists = todoList.filter(item => {
          if (currentDate === item.date) {
            return true;
          }
          return false;
        });
        console.log(todoList);
        if (todoLists.length !== 0) {
          this.setState({
            markedDate: markDot,
            todoList: todoLists[0].todoList,
          });
        } else {
          this.setState({
            markedDate: markDot,
            todoList: [],
          });
        }
      }
    } catch (error) {
      // Error retrieving data
    }
  };
  renderCalendarWithSelectableDate = () => {
    const { selected, currentDate, todoList } = this.state;
    const { navigation } = this.props;
    console.log(todoList);
    return (
      <Fragment>
        <Fragment>
          <Calendar
            current={new Date()}
            // style={styles.calendar}
            hideExtraDays
            onDayPress={this.onDayPress}
            markedDates={{
              [selected]: {
                selected: true,
                disableTouchEvent: true,
                selectedColor: 'orange',
                selectedTextColor: 'white',
              },
            }}
          />
          <TouchableOpacity
            onPress={() =>
              navigation.navigate('createTask', {
                currentDate,
              })
            }
            style={styles.viewTask}
          >
            <Image
              source={require('../../assets/plus.png')}
              style={{
                height: 30,
                width: 30,
              }}
            />
          </TouchableOpacity></Fragment>
        <View
          style={{
            width: '100%',
            height: Dimensions.get('window').height - 170,
          }}
        >
          <ScrollView
            contentContainerStyle={{
              paddingBottom: 20,
            }}
          >
            {todoList.map(item => (
              <TouchableOpacity
                onPress={() => {
                  this.setState(
                    {
                      selectedTask: item,
                      isModalVisible: true,
                    },
                    () => {
                      this._getEvent();
                    }
                  );
                }}
                key={item.key}
                style={styles.taskListContent}
              >
                <View
                  style={{
                    marginLeft: 13,
                  }}
                >
                  <View
                    style={{
                      flexDirection: 'row',
                      alignItems: 'center',
                    }}
                  >
                    <View
                      style={{
                        height: 12,
                        width: 12,
                        borderRadius: 6,
                        backgroundColor: item.color,
                        marginRight: 8,
                      }}
                    />
                    <Text
                      style={{
                        color: '#554A4C',
                        fontSize: 20,
                        fontWeight: '700',
                      }}
                    >
                      {item.title}
                    </Text>
                  </View>
                  <View>
                    <View
                      style={{
                        flexDirection: 'row',
                        marginLeft: 20,
                      }}
                    >
                      <Text
                        style={{
                          color: '#BBBBBB',
                          fontSize: 14,
                          marginRight: 5,
                        }}
                      >{`${moment(item.alarm.time).format(
                        'YYYY'
                      )}/${moment(item.alarm.time).format('MM')}/${moment(
                        item.alarm.time
                      ).format('DD')}`}</Text>
                      <Text
                        style={{
                          color: '#BBBBBB',
                          fontSize: 14,
                        }}
                      >
                        {item.notes}
                      </Text>
                    </View>
                  </View>
                </View>
                <View
                  style={{
                    height: 80,
                    width: 5,
                    backgroundColor: item.color,
                    borderRadius: 5,
                  }}
                />
              </TouchableOpacity>
            ))}
          </ScrollView>
        </View>
      </Fragment>
    );
  };
  render() {
    return (
      <SafeAreaView showsVerticalScrollIndicator={false} >
        { this.renderCalendarWithSelectableDate()}
      </SafeAreaView>
    );
  };
}
export default connect(state => ({
  loading: state.loading,
  effects: state.loading.effects
}))(CalendarPage);

const styles = StyleSheet.create({
  calendar: {
    marginBottom: 10,
  },
  text: {
    textAlign: 'center',
    padding: 10,
    backgroundColor: 'lightgrey',
    fontSize: 16,
  },
  viewTask: {
    position: 'absolute',
    top: 400,
    right: 17,
    height: 50,
    width: 50,
    backgroundColor: 'orange',
    borderRadius: 30,
    justifyContent: 'center',
    alignItems: 'center',
    shadowColor: '#2E66E7',
    // shadowOffset: {
    //   width: 0,
    //   height: 5,
    // },
    // shadowRadius: 30,
    shadowOpacity: 0.5,
    elevation: 5,
    zIndex: 999,
  },
});
