import React from 'react';
import CalendarStrip from 'react-native-calendar-strip';
import moment from 'moment';
import { View, StyleSheet } from 'react-native';
import dva, { connect } from 'dva';
const styles = StyleSheet.create({
  container: { flex: 1 }
});
class CalendarPage extends React.PureComponent {
  state = {
    datesWhitelist: [
      {
        start: moment(),
        end: moment().add(365, 'days'), // total 4 days enabled
      },
    ],
    todoList: [],
    markedDate: [],
    currentDate: `${moment().format('YYYY')}-${moment().format(
      'MM'
    )}-${moment().format('DD')}`,
  }

  _updateCurrentTask = async currentDate => {
    try {
      const value = await AsyncStorage.getItem('TODO');
      if (value !== null) {
        const todoList = JSON.parse(value);
        const markDot = todoList.map(item => item.markedDot);
        const todoLists = todoList.filter(item => {
          if (currentDate === item.date) {
            return true;
          }
          return false;
        });
        if (todoLists.length !== 0) {
          this.setState({
            markedDate: markDot,
            todoList: todoLists[0].todoList,
          });
        } else {
          this.setState({
            markedDate: markDot,
            todoList: [],
          });
        }
      }
    } catch (error) {
      // Error retrieving data
    }
  };
  // Local path to file on the device
  render() {
    const { dispatch, effects } = this.props;
    const { state: {
      datesWhitelist,
      markedDate,
      currentDate,
    } } = this;
    return (
      <View>
        <CalendarStrip
          ref={ref => {
            this.calenderRef = ref;
          }}
          calendarAnimation={{ type: 'sequence', duration: 30 }}
          daySelectionAnimation={{
            type: 'background',
            duration: 200,
            highlightColor: '#ffffff',
          }}
          style={{
            height: 150,
            paddingTop: 20,
            paddingBottom: 20,
          }}
          calendarHeaderStyle={{ color: '#000000' }}
          dateNumberStyle={{ color: '#000000', paddingTop: 10 }}
          dateNameStyle={{ color: '#BBBBBB' }}
          highlightDateNumberStyle={{
            marginTop: 10,
            height: 35,
            width: 35,
            textAlign: 'center',
            borderRadius: 17.5,
            overflow: 'hidden',
            fontWeight: '400',
            justifyContent: 'center',
            alignItems: 'center',
          }}
          highlightDateNameStyle={{ color: '#2E66E7' }}
          disabledDateNameStyle={{ color: 'grey' }}
          disabledDateNumberStyle={{ color: 'grey', paddingTop: 10 }}
          datesWhitelist={datesWhitelist}
          iconLeft={require('../../assets/left-arrow.png')}
          iconRight={require('../../assets/right-arrow.png')}
          iconContainer={{ flex: 0.1 }}
          markedDates={markedDate}
          onDateSelected={date => {
            const selectedDate = `${moment(date).format('YYYY')}-${moment(
              date
            ).format('MM')}-${moment(date).format('DD')}`;
            this._updateCurrentTask(selectedDate);
            this.setState({
              currentDate: selectedDate,
            });
          }}
        />
        Calendar
      </View>
    )
  }
}
export default connect(state => ({
  loading: state.loading,
  effects: state.loading.effects
}))(CalendarPage);
